import React from "react";

class Stopwatch extends React.Component {
  constructor(props) {
    this.state = {
      timerOn: false,
      timerStart: 0,
      timerTime: 0
    };
  }

  startTimer = () => {
    this.setState({
      timerOn: true,
      timerTime: this.state.startTimer,
      timerStart: Date.now() - this.state.timerTime
    });

    this.timer = setInterval(() => {
      this.setState({ timerTime: Date.now() - this.state.startTimer });
    }, 10);
  };

  stopTimer = () => {
    this.setState({ timerOn: false });
    clearInterval(this.timer);
  };

  resetTimer = () => {
    this.setState({ timerOn: false, timerTime: 0, timerStart: 0 });
  };

  render() {
    const { timerTime } = this.state;
    let centiseconds = ("0" + (Math.floor(timerTime / 10) % 100)).slice(-2);
    let seconds = ("0" + (Math.floor(timerTime / 1000) % 60)).slice(-2);
    let minutes = ("0" + (Math.floor(timerTime / 60000) % 60)).slice(-2);
    let hours = ("0" + Math.floor(timerTime / 3600000)).slice(-2);

    return (
      <div>
        <div>
          {hours} : {minutes} : {seconds} : {centiseconds}
        </div>
        <div>
          {this.state.timerOn === false && this.state.timerTime === 0 && (
            <button className="ui green button" onClick={this.startTimer}>
              {" "}
              Start{" "}
            </button>
          )}
          {this.state.timerOn && (
            <button className="ui labeled icon button" onClick={this.stopTimer}>
              {" "}
              <i className="pause icon "></i> Stop{" "}
            </button>
          )}
          {this.state.timerOn === false && this.state.timerTime > 0 && (
            <button className="ui blue button" onClick={this.startTimer}>
              {" "}
              Resume{" "}
            </button>
          )}
          {this.state.timerOn === false && this.state.timerTime > 0 && (
            <button className=" ui red button" onClick={this.resetTimer}>
              {" "}
              Reset{" "}
            </button>
          )}
        </div>
      </div>
    );
  }
}

export default Stopwatch;
